				#-------------------------------------------------------------------------#
				# Verificar si el registro existe en la tabla
				$sql_id = $this->db->select('*', t_azp_fct_prefacture, d_azp_fct_prefacture_id_azp_fct_prefacture . " = '" . $empty_id . "' AND " . d_azp_fct_prefacture_m_deleted . " = '2'");
